import { LOGIN_SUCCESS, LOGIN_FAILED, LOGOUT_SUCCESS,sing } from '../types/authTypes';
export const logIn = credentials => {
    return (dispatch, getState, { getFirebase }) => {
        const firebase = getFirebase();
        firebase.auth().signInWithEmailAndPassword(
            credentials.email,
            credentials.password
        ).then(() => {
            dispatch({
                type: LOGIN_SUCCESS
            });
        }).catch((err) => {
            dispatch({
                type: LOGIN_FAILED,
                err
            })
        })
    }
}

export const logOut = () => {
    return (dispatch, getState, { getFirebase }) => {
        const firebase = getFirebase();
        firebase.auth().signOut().then(() => {
            dispatch({
                type: LOGOUT_SUCCESS
            })
        })
    }
}
